import React, { useEffect, useState } from "react";
import "../game/css/game.css";

import Lobby from "./components/lobby.tsx";
import GameMain from "./components/gameMain.jsx";
import expectation from "./mainFn/expectation.ts";
import finishTurn from "./responce/sendFinishTurn.ts";


// game
const Game = () => {
	const [game, setGame] = useState(JSON.parse(localStorage.getItem("game_status") || ''))
	const [players, setPlayers] = useState([JSON.parse(localStorage.getItem("user") || '')])
	const [expectationState, setExpectationState] = useState(true)
	const [emoji, setEmoji] = useState(null)

	useEffect(() => { expectation({ setPlayers, setGame, setEmoji, setExpectationState }) }, [])

	useEffect(() => {
		let interval = null

		if (expectationState) {
			interval = setInterval(() => {
				const gameId = JSON.parse(localStorage.getItem("game_status") || '').gameId
				finishTurn(gameId).catch()
			}, 1000)
		} else {
			clearInterval(interval)
		}
	}, [expectationState])

	return (
		<section
			className="game"
			style={{
				// backgroundImage: `url(/res/skins${activeCosmetic?.find((item) => item.cosmetic?.type === "table")
				// 	?.cosmetic?.link
				// 	})`,
				backgroundRepeat: "no-repeat",
				backgroundSize: "cover",
			}}
		>
			{expectationState && <Lobby game={game} players={players} setExpectation={setExpectationState} />}
			{!expectationState && <GameMain game={game} players={players} emoji={emoji} setEmoji={setEmoji} />}
		</section>
	);
};

export default Game;
