import React, { useEffect, useState } from "react";
import imgAvatar from '../../media/img/avatar.png'

function Players({players, emoji}) {
	const [selectedEmoji, setSelectedEmoji] = useState(null);
	const [selectedEmojiClass, setSelectedEmojiClass] = useState("");

	useEffect(()=>{
		setSelectedEmoji(emoji?.emoji)
		setSelectedEmojiClass("hide")
		
		setTimeout(() => {
			setSelectedEmoji(null);
			setSelectedEmojiClass("");
		}, 2250);
	
		return () => {
			// clearTimeout(clearTimeoutFn);
		};
	}, [emoji])

	return <div className="players">
		{players.map((player) => {
			let topPosition = "0px";

			return (
				<div
					className="player"
					id={player.id}
					key={player.id}
					style={{ position: "relative", marginTop: topPosition }}
				>
					<div className="picture">
						<img
							className="profile"
							src={player.avatar || imgAvatar}
							alt="player_picture"
						/>
					</div>
					<span className="player_name">{player.username || player.user.username}</span>
					{selectedEmoji && (
						<div className={`selected_emoji ${selectedEmojiClass}`}>
							<img src={selectedEmoji} alt="Selected Emoji" />
						</div>
					)}
				</div>
			);
		})}
	</div>
}

export default Players
