import React, { useEffect } from "react";
import { animateGetCardsPlayerSelf } from "../utils/animationUtils";

function Cards ({game, cardRefs}){
	useEffect(()=>{
		if(game.players){
			const userId = JSON.parse(localStorage.getItem('user') || '').id
			const usersCards: Array<any> = []
			game.players.forEach(el=>{
				usersCards.push({id: el.id, cards: el.cards})
			})
			console.log(cardRefs, usersCards)
			const distribution = []
			cardRefs.forEach(el=>{
				const typeCard = el.current.data
				console.log(typeCard)
			})
			animateGetCardsPlayerSelf(
				cardRefs.map(el=>el.current),
				100,
				100,
				1,
				0.3,
				0
			)
		}
	})
	return (
		<div className="player_self">
			<div className="cards"></div>
		</div>
	)
}
export default Cards
