import React, { useEffect, useState } from "react";
import "../game/css/game.css";

import Lobby from "./components/lobby.tsx";
// animate Utils
import {
	animateMoveTo,
	animateGetCardsPlayerSelf,
	animateShowTrumpCard,
} from "./utils/animationUtils";

import expectation from "./mainFn/expectation.ts";


// game
const Game = () => {
	const [game, setGame] = useState(JSON.parse(localStorage.getItem("game_status") || ''))
	const [players, setPlayers] = useState([JSON.parse(localStorage.getItem("user") || '')])
	useEffect(() => { expectation({ setPlayers, setGame }) }, [])


	// render
	return (
		<section
			className="game"
			style={{
				// backgroundImage: `url(/res/skins${activeCosmetic?.find((item) => item.cosmetic?.type === "table")
				// 	?.cosmetic?.link
				// 	})`,
				backgroundRepeat: "no-repeat",
				backgroundSize: "cover",
			}}
		>
			<Lobby game={game} players={players} />
			{/* <Timer game={game} /> */}

			{/* change_card */}
			{/* {(game?.status !== "await" && game?.status !== "load") && (<span className="change_card"></span>)} */}

			<span className="enemy_card"></span>

			{/* {deck.map((card, index) => (
				<GameCard
					style={{ zIndex: deck.length - index }} // Передаем объект стиля
					ref={(el) => (cardRefs.current[index] = el)}
					key={card.id}
					id={card.id}
					type={card.type}
					name={card.name}
					value={card.value}
				></GameCard>
			))} */}

			{/* player_self */}
			{/* <div className="player_self" ref={playerSelfRef}>
				<div className="cards"></div>
			</div> */}
			{/* game control buttons */}
			{/* {(game?.status !== "await" && game?.status !== "load") && (
				<div className="control_btns">
					<button
						className="cheat"
						onClick={toggleTimer}
						disabled={!buttonStates.cheat}
					>
						<I18nText path="cheat_button" />
					</button>
					<button className="take" disabled={!buttonStates.take}>
						<I18nText path="take_button" />
					</button>
					<button
						className="pass"
						disabled={!buttonStates.pass}
						onClick={finishTurn}
					>
						<I18nText path="pass_button" />
					</button>
					<button
						className="react"
						onClick={toggleEmojiPopup}
						disabled={!buttonStates.react}
					>
						<I18nText path="react_button" />
					</button>
				</div>
			)} */}




			{/* Popup с эмодзи */}
			{/* <EmojiPopup onSelectEmoji={selectEmoji} show={showEmojiPopup} />

			{selectedEmoji && (
				<div className={`selected_emoji ${selectedEmojiClass}`}>
					<img src={selectedEmoji} alt="Selected Emoji" />
				</div>
			)} */}
		</section>
	);
};

export default Game;
