import React, { useEffect, useState } from "react";
import imgAvatar from '../../media/img/avatar.png'

function Players({game, players, emoji, setExpectation}) {
	const [selectedEmoji, setSelectedEmoji] = useState(null);
	const [selectedEmojiClass, setSelectedEmojiClass] = useState("");

	useEffect(()=>{
		setSelectedEmoji(emoji?.emoji)
		setSelectedEmojiClass("active");
		
		setTimeout(()=>{setSelectedEmojiClass("");}, 2000)
		setTimeout(() => {setSelectedEmoji(null);}, 2250);
	}, [emoji])

	useEffect(()=>{
		if(game){
			if(game['playerAmount'] == game.players.length){
				setExpectation(false)
			}
		}
	}, [game, players])

	return <div className="players">
		{players.map((player) => {
			let topPosition = "0px";

			return (
				<div
					className="player"
					id={player.id}
					key={player.id}
					style={{ position: "relative", marginTop: topPosition }}
				>
					<div className="picture">
						<img
							className="profile"
							src={player.avatar || imgAvatar}
							alt="player_picture"
						/>
					</div>
					<span className="player_name">{player.username || player.user.username}</span>

					<div className={`selected_emoji user ${selectedEmojiClass}`}>
						{selectedEmoji && <img src={selectedEmoji || ''} alt="Selected Emoji" />}
					</div>
				</div>
			);
		})}
	</div>
}

export default Players
