import React, { useEffect, useState } from "react";
import axios from 'axios'
import config from "../../config";
import GameCard from '../res/components/gameCard.jsx'
import PlayerButtons from "./playerButtons.tsx";
import Emoji from "./emoji.tsx";
import Cards from "./cards.tsx";
import Players from "./players.tsx";


function GameMain({ game }) {
	const [cardRefs, setCardRefs] = useState([]);
	const [allDeck, setAllDeck] = useState([])

	useEffect(() => {
		if (game) {
			responseStartGame(game)
			const allDeck = genAllDeck(game)
			setAllDeck(allDeck)
		}
	}, [game])

	useEffect(() => { }, [allDeck])


	return <>
		<Players />
		{(game?.status !== "await" && game?.status !== "load") && (<span className="change_card"></span>)}
		<span className="enemy_card"></span>

		{allDeck.map((card, index) => (
			<GameCard
				addRef={setCardRefs}
				style={{ zIndex: game.deck.length - index }}
				key={`${index}${card.name}${card.value}`}
				name={card.name}
				value={card.nominal}
			></GameCard>
		))}

		<Cards game={game} cardRefs={cardRefs} />
		<PlayerButtons />
		<Emoji />
	</>
}
export default GameMain

function responseStartGame(game) {
	const body = { gameId: game.gameId }
	axios.post(`${config.url}/game/start-game`, body, {
		headers: {
			"Access-Control-Expose-Headers": "X-Session",
			"X-Session": localStorage.getItem("session_key"),
		},
	}
	).then((res) => {
		console.log(res)
		// setGame((prevGame) => ({
		// 	...prevGame,
		// 	status: "start",
		// }));
		// localStorage.setItem("game_status", JSON.stringify(res.data));
		// localStorage.setItem("session_key", res.headers.get("X-Session"));
		// setFullGameDeck(res.data);
	})
		.catch((err) => {
			console.log(err)
			// localStorage.setItem(
			// 	"session_key",
			// 	err.response.headers.get("X-Session")
			// );
		});
}
function genAllDeck(game) {
	if (!game.deck) return []
	let deck = [...game.deck]

	game.players.forEach(el => deck = [...deck, ...el.cards])
	return deck
}
