import React from "react";
import GameCard from "../res/components/gameCard/gameCard";

function EnemyCard ({game, enemyCardRef}){
	const boxWidth = enemyCardRef?.current?.getBoundingClientRect().width
	let length = game?.attackerCardsFromMap?.length
	return (
		<span className="enemy_card" ref={enemyCardRef}
			style={{
				left: `-${length * (boxWidth/2)}px`
			}}
		>
			{game?.defenderCardsFromMap?.map((card, index)=>(
				card &&
				<GameCard
					addRef={()=>{}}
					type={''}
					style={{ 
						zIndex: game.deck.length - index, 
						transform: `translate(${
							length > 3 ? 100*index : 110*index
						}%) scale(${
							length > 3 ? 0.9 : 1
						})`
					}}
					key={`${index}${card.name}${card.value}`}
					name={card.name}
					value={card.nominal}
					changeCardRef={enemyCardRef}
					className="game_card_box open-card change_pos"
					typeCard='onTable'
				></GameCard>
			))}
		</span>
	)
}

export default EnemyCard
