import sendWalking from "../../../responce/sendWalking.ts"
import { animateMoveTo, animateVibrateCard } from "../../../utils/animationUtils"

type TlastCardActive = {name:string,value: string,ref:React.MutableRefObject<HTMLElement> | null, lastX: number, lastY: number}
let lastCardActive:TlastCardActive = { 
	name: '', value: '', ref: null,
	lastX: 0, lastY: 0
}

function cardClick(e, name, value, refCard, changeCardRef) {
	if (e.target.dataset.side == 'back' && e.target.dataset.inTable != 'true') {
		if (refCard.current) {

			if (lastCardActive['name'] == name) {
				const gameId = JSON.parse(localStorage.getItem('game_status') || '').gameId

				const code = sendWalking(gameId, name, value).catch(err=>{
					err.status == 400 && animateVibrateCard(refCard.current)
				})
			} else {
				const lastRef = lastCardActive['ref']
				if (lastRef && lastRef.current) {
					animateMoveTo(
						lastRef.current,
						lastCardActive.lastX, lastCardActive.lastY,
						1, 0.3, 0, false
					)
				}

				const { y, x, height } = refCard.current.getBoundingClientRect()
				animateMoveTo(
					refCard.current,
					x, y - height / 4,
					1, 0.3, 0, false
				)
				lastCardActive = { name, value, ref: refCard, lastX: x+4, lastY: y+4 }
			}

		}
	}
}

export default cardClick
