import React, { useEffect, useRef, useState } from "react";
import { cards } from "../../includes/cards";

const GameCard = (
	({ id, type, name, value, style, addRef, ...props }) => {
		const refCard = useRef(null)
		const [userCosmetic] = useState(
			JSON.parse(localStorage.getItem("user_cosmetic"))
		);
		useEffect(() => {
			addRef(p => { p.push(refCard); return [...p] })
		}, [])

		return (
			<div className="game_card_box">
				<img className="game_card_front" src={`url(/res/skins${userCosmetic?.find((item) => item.cosmetic?.type === "card")?.cosmetic?.link})`} alt=""></img>
				<img
					ref={refCard}
					className={`game_card ${name}`}
					data-name={name}
					data-nominal={value}
					{...props}
					style={{
						...style,
						backgroundImage: `url(/res/skins${userCosmetic?.find((item) => item.cosmetic?.type === "card")?.cosmetic?.link})`,
					}}
					alt=""
					src={cards[`${name[0].toLowerCase()}${value}`]}
				></img>
			</div>
		);
	}
);

export default GameCard;
