import React, { forwardRef, useEffect, useRef, useState } from "react";

const GameCard = forwardRef(
	({ id, type, name, value, style, addRef, ...props }) => {
		const refCard = useRef(null)
		const [userCosmetic] = useState(
			JSON.parse(localStorage.getItem("user_cosmetic"))
		);
		useEffect(() => {
			console.log(addRef, refCard)
			addRef(p => { p.push(refCard); return p })
		}, [])

		return (
			<img
				ref={refCard}
				className={`game_card ${name}`}
				data-name={name}
				data-type={type}
				data-value={value}
				data-id={id}
				{...props}
				style={{
					...style,
					backgroundImage: `url(/res/skins${userCosmetic?.find((item) => item.cosmetic?.type === "card")
						?.cosmetic?.link
						})`,
				}}
				alt=""
			></img>
		);
	}
);

export default GameCard;
