import React, { useEffect, useRef, useState } from "react";
import pathImage from '../fullcard/pathImage.ts'

const GameCard = (
	({ id, type, name, value, style, addRef, ...props }) => {
		const refCard = useRef(null)
		const [userCosmetic] = useState(
			JSON.parse(localStorage.getItem("user_cosmetic"))
		);
		useEffect(() => {
			addRef(p => { p.push(refCard); return [...p] })
		}, [])

		return (
			<img
				ref={refCard}
				className={`game_card ${name}`}
				data-name={name}
				data-nominal={value}
				{...props}
				style={{
					...style,
					backgroundImage: `url(/res/skins${userCosmetic?.find((item) => item.cosmetic?.type === "card")?.cosmetic?.link})`,
				}}
				alt=""
				src={pathImage[`${name[0].toLowerCase()}${value}`]}
			></img>
		);
	}
);

export default GameCard;
