import React, { useEffect, useRef, useState } from "react";

function Timer ({game}){
	const timerRef = useRef(null) as any
	const [init, setInit] = useState(true)

	useEffect(()=>{
		if(init && timerRef.current){
			let counter = 3
			const interval = setInterval(()=>{
				timerRef.current.innerHTML = counter
				counter--
				if(counter == 0){
					clearInterval(interval)
					setInit(false)
				}
			}, 1000)
		}
	}, [game])

	if(game['playerAmount'] == game.players.length){
		return (
			<div className="timer timer_fast_active" >
				<span className="time" ref={timerRef}></span>
			</div>
		);
	}else{
		return ''
	}
};

export default Timer;
