import React, { useEffect, useRef, useState } from "react";
import axios from 'axios'
import config from "../../config";
import PlayerButtons from "./playerButtons.tsx";
import Emoji from "./emoji.tsx";
import Cards from "./cards.tsx";
import Players from "./players.tsx";
import Timer from "./initTimer.tsx";
import GameCard from "../res/components/gameCard/gameCard.jsx";
import ChangeCard from "./changeCard.tsx";
import EnemyCard from "./enemyCard.tsx";


function GameMain({ game, players }) {
	const changeCardRef = useRef(null)
	const enemyCardRef = useRef(null)
	const [cardRefs, setCardRefs] = useState([]);
	const [allDeck, setAllDeck] = useState([])
	const userId = JSON.parse(localStorage.getItem('user')).id

	useEffect(() => {
		if (game) {
			responseStartGame(game)
			const allDeck = genAllDeck(game)
			setAllDeck(allDeck)
		}
	}, [game])

	useEffect(() => { }, [allDeck])


	return <>
		<Players players={players.filter(el => el.id != userId)} />
		<Timer game={game} type="game" />
		<ChangeCard game={game} changeCardRef={changeCardRef} />
		<EnemyCard game={game} enemyCardRef={enemyCardRef} />

		{allDeck.map((card, index) => (
			<GameCard
				addRef={setCardRefs}
				style={{ zIndex: game.deck.length - index }}
				key={`${index}${card.name}${card.value}`}
				name={card.name}
				value={card.nominal}
				changeCardRef={changeCardRef}
			></GameCard>
		))}

		<Cards game={game} cardRefs={cardRefs} />
		<PlayerButtons />
		<Emoji />
	</>
}
export default GameMain

function responseStartGame(game) {
	const body = { gameId: game.gameId }
	axios.post(`${config.url}/game/start-game`, body, {
		headers: {
			"Access-Control-Expose-Headers": "X-Session",
			"X-Session": localStorage.getItem("session_key"),
		},
	}
	).then((res) => {
		console.log(res)
		// setGame((prevGame) => ({
		// 	...prevGame,
		// 	status: "start",
		// }));
		// localStorage.setItem("game_status", JSON.stringify(res.data));
		// localStorage.setItem("session_key", res.headers.get("X-Session"));
		// setFullGameDeck(res.data);
	})
		.catch((err) => {
			console.log(err)
			// localStorage.setItem(
			// 	"session_key",
			// 	err.response.headers.get("X-Session")
			// );
		});
}
function genAllDeck(game) {
	if (!game.deck) return []
	let deck = [...game.deck]

	game.players.forEach(el => deck = [...deck, ...el.cards])
	return deck
}
